import ReactMarkdown from "react-markdown";
import Moment from "react-moment";
import { fetchAPI } from "../../lib/api";
import Layout from "../../components/layout";
import Image from "../../components/image";
import Seo from "../../components/seo";
import { getStrapiMedia } from "../../lib/media";

const Applicant = ({ applicants }) => {
  const { address, age, name, email, stage } = applicants;
  //   const seo = {
  //     metaTitle: applicants.title,
  //     metaDescription: applicants.description,
  //     shareImage: applicants.image,
  //     applicants: true,
  //   };
  console.log(applicants);
  return (
    <Layout>
      {/* Utökad info om varje applicant */}

      <div className="uk-container">
        <div className=" uk-flex uk-flex-middle uk-flex-between">
          <h1 id="category" className="uk-text-uppercase">
            {applicant.name}
          </h1>
          <Image
            image={applicant.image}
            style={{
              position: "static",
              borderRadius: "50%",
              height: 120,
              width: 120,
              objectFit: "cover",
            }}
          />
        </div>
        <ul>
          <li>{address}</li>
          <li>{age} år</li>
          <li>{email}</li>
          <li>{stage.name}</li>
        </ul>
      </div>
    </Layout>
  );
};

export async function getStaticPaths() {
  const applicants = await fetchAPI("/applicants");
  return {
    paths: applicants.map((applicants) => ({
      params: {
        slug: applicants.slug,
      },
    })),
    fallback: false,
  };
}

export async function getStaticProps({ params }) {
  const applicants = await fetchAPI(`/applicants?slug=${params.slug}`);

  return {
    props: { applicants: applicants[0] },
    revalidate: 1,
  };
}

export default Applicant;
