import ReactMarkdown from "react-markdown";
import { fetchAPI } from "../../lib/api";
import Layout from "../../components/layout";
import Image from "../../components/image";
import { createAPI } from "../../lib/api";
import { useRef } from "react";
const Create = ({ allStages, create }) => {
  const nameRef = useRef(null);
  const ageRef = useRef(null);
  const mailRef = useRef(null);
  const addressRef = useRef(null);
  const descRef = useRef(null);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(nameRef.current.value);

    const newApplicant = {
      name: nameRef.current.value,
      age: ageRef.current.value,
      email: mailRef.current.value,
      address: addressRef.current.value,
      description: descRef.current.value,
      stage: allStages[0],
      image: {
        url:
          "https://images.mentor.se/wp-content/uploads/2020/03/19074104/Profile-placeholder.png",
      },
    };

    createAPI("/applicants", newApplicant);
  };

  return (
    <Layout categories={allStages}>
      <div className="uk-container uk-margin-small-top">
        <h1>{create.heading}</h1>
        <form onSubmit={(e) => handleSubmit(e)}>
          <legend className="uk-legend">Namn</legend>
          <input
            className="uk-input uk-form-width-large uk-margin-small-bottom"
            placeholder="Anna Andersson"
            ref={nameRef}
          />
          <legend className="uk-legend">Ålder</legend>
          <input
            className="uk-input uk-form-width-small uk-margin-small-bottom"
            placeholder="99"
            ref={ageRef}
          />
          <legend className="uk-legend">Email</legend>
          <input
            className="uk-input uk-form-width-large uk-margin-small-bottom"
            placeholder="anna.andersson@gmail.com"
            ref={mailRef}
          />
          <legend className="uk-legend">Adress</legend>
          <input
            className="uk-input uk-form-width-large uk-margin-small-bottom"
            placeholder="Gågatan 1, 123 45, Stockholm"
            ref={addressRef}
          />
          <legend className="uk-legend">Beskrivning</legend>
          <textarea
            className="uk-textarea uk-margin-small-bottom"
            ref={descRef}
          ></textarea>

          <button type="submit">Submit</button>
        </form>
      </div>
    </Layout>
  );
};

export async function getStaticProps() {
  const create = await fetchAPI("/create");
  const allStages = await fetchAPI("/stages");

  return {
    props: { allStages: allStages, create: create },
    revalidate: 1,
  };
}

export default Create;
