import ReactMarkdown from "react-markdown";
import Moment from "react-moment";
import { fetchAPI } from "../../lib/api";
import Layout from "../../components/layout";
import Image from "../../components/image";
import Seo from "../../components/seo";
import { getStrapiMedia } from "../../lib/media";

const Stages = ({ stages }) => {
  const seo = {
    metaTitle: stages.title,
    metaDescription: stages.description,
    shareImage: stages.image,
    stages: true,
  };
  console.log(stages);
  return (
    <Layout categories={stages}>
      <Seo seo={seo} />
    </Layout>
  );
};

export async function getStaticPaths() {
  const stages = await fetchAPI("/stages");
  console.log(stages);

  return {
    paths: stages.map((stages) => ({
      params: {
        slug: stages.slug,
      },
    })),
    fallback: false,
  };
}

export async function getStaticProps({ params }) {
  console.log(params);

  const stages = await fetchAPI(`/stages?slug=${params.Slug}`);

  return {
    props: { stages: stages[0] },
    revalidate: 1,
  };
}

export default Stages;
