import React, { useEffect, useState } from "react";
import Applicants from "../components/applicants";
import Layout from "../components/layout";
import Seo from "../components/seo";
import { fetchAPI } from "../lib/api";

const Home = ({ homepage, applicants, stages }) => {
  const [applicantList, setApplicantList] = useState([]);
  useEffect(() => {
    setApplicantList(applicants);
  }, [applicants]);
  console.log(applicantList);
  const handleSearch = (e) => {
    console.log(e.target.value);
    const bob = fetchAPI(`/applicants?name_contains=${e.target.value}`);
    setApplicantList(bob);
  };

  return (
    <Layout categories={stages}>
      <Seo seo={homepage.seo} />
      <div className="uk-section">
        <div className="uk-container">
          <h1>{homepage.hero.title}</h1>
          <input
            className="uk-input"
            placeholder="Sök på namn"
            onChange={(e) => handleSearch(e)}
          />
          <Applicants applicants={applicantList} showStep />
        </div>
      </div>
    </Layout>
  );
};

export async function getStaticProps() {
  // Run API calls in parallel
  const [applicants, stages, homepage] = await Promise.all([
    fetchAPI("/applicants"),
    fetchAPI("/stages"),
    fetchAPI("/homepage"),
  ]);

  return {
    props: { applicants, stages, homepage },
    revalidate: 1,
  };
}

export default Home;
